import React, { useEffect, useState } from "react";
import Snackbar from "@mui/material/Snackbar";
import { SERVER_URL } from "../constants.js";
import { DataGrid } from "@mui/x-data-grid";
import Button from "@mui/material/Button";
import AddCar from "./AddCar.js";
import EditCar from './EditCar.js';

function CarList() {
  const [cars, setCars] = useState([]);
  const [open, setOpen] = useState(false);
  useEffect(() => {
    fetchCars();
  }, []);

  const fetchCars = () => {
    fetch(SERVER_URL + "api/cars")
      .then((response) => response.json())
      .then((data) => setCars(data._embedded.cars))
      .catch((err) => console.error(err));
  };

  const addCar = car => {
    fetch(SERVER_URL + "api/cars", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(car),
    }).then((response) => {
      if (response.ok) {
        fetchCars();
      } else {
        alert("Something went wrong !!");
      }
    }).catch(err => console.error(err));
  };

  const updateCar = (car, link) => {
    fetch(link, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(car),
    }).then((response) => {
      if (response.ok) {
        fetchCars();
      } else {
        alert("Something went wrong !!");
      }
    }).catch(err => console.error(err));
  };

  const onDelClick = url => {
    if (window.confirm("Are you sure to delete ?")) {
      fetch(url, { method: "DELETE" })
        .then((response) => {
          if (response.ok) {
            fetchCars();
            setOpen(true);
          } else {
            alert("Some thing is wrong !!!!");
          }
        })
        .catch((Err) => console.error(Err));
    }
  };
  const columns = [
    { field: "brand", headerName: "Marque", width: 200 },
    { field: "model", headerName: "Modèle", width: 200 },
    { field: "color", headerName: "Couleur", width: 200 },
    { field: "year", headerName: "Année", width: 150 },
    { field: "price", headerName: "Prix", width: 150 },
    {
      field: "_links.car.href",
      headerName: "",
      sortable: false,
      filterable: false,
      renderCell: row => <EditCar data={row} updateCar={updateCar}/>
    },
    {
      field: "_links.self.href",
      headerName: "",
      sortable: false,
      filterable: false,
      renderCell: (row) => (
        <Button onClick={() => onDelClick(row.id)}>Delete</Button>
      ),
    },
  ];

  return (
    <React.Fragment>
      <AddCar addCar={addCar}/>
      <div style={{ height: 500, width: "100%" }}>
        <DataGrid
          rows={cars}
          disableRowSelectionOnClick={true}
          columns={columns}
          getRowId={(row) => row._links.self.href}
        />
        <Snackbar
          open={open}
          autoHideDuration={2000}
          onClose={() => setOpen(false)}
          message="voiture suprimee"
        />
      </div>
    </React.Fragment>
  );
}
export default CarList;

